#Write a class "BankAccount" with attributes: account_number, balance, and account_holder. Add methods to deposit and withdraw fundsclass BankAccount:    def __init__(self, acc_num, bal, acc_hold):        self.account_number=int(acc_num)        self.balance=float(bal)        self.account_holder=str(acc_hold)    def deposit(self):        mon=float(input("Enter sum to be deposited:"))        print("Money deposited")        self.balance+=mon        print("Your balance:", self.balance)    def withdraw(self):        mon=float(input("Enter sum to withdraw:"))        if mon<self.balance:            print("Money withdrawed")            self.balance-=mon            print("Your balance:", self.balance)        else:            print("Not enough balance!!!")            def choice():    print("MENU\n1. Deposit\n2.Withdraw\n3.EXIT")    ch=int(input("Enter choice:"))    return ch#MAINacc_num=int(input("Enter account number:"))acc_hold=input("Enter account holder's name:")bank=BankAccount(acc_num, 0, acc_hold)while True:    ch=choice()    if ch==1:        bank.deposit()    elif ch==2:        bank.withdraw()    elif ch==3:        break    else:        print("Enter valid choice!!!")