#Define a class "Car" with attributes: brand, model, and year. Add methods to display car information and calculate age.class Car:    def __init__(self, bnd, mod, y):        self.brand=str(bnd)        self.model=str(mod)        self.year=int(y)    def car_info(self):        print("Details-> Brand:", self.brand, ",Model:", self.model, ",Year:", self.year)    def age(self):        return 2024-self.year    def choice():    print("MENU\n1. Details\n2.Age\n3.EXIT")    ch=int(input("Enter choice:"))    return ch#MAINbrand=input("Enter brand:")model=input("Enter model:")yr=int(input("Enter year:"))car=Car(brand, model, yr)while True:    ch=choice()    if ch==1:        car.car_info()    elif ch==2:        print("Age of the car is:", car.age())    elif ch==3:        break    else:        print("Enter valid choice!!!")